form.css:
.status0{
	position:relative;
	display: inline-block; 
	background:url('img/status/ztt.png');
	background-size:400% 400%;
	background-position:0px 0px;
}
.status1{
	position:relative;
	display: inline-block;
	background:url('img/status/ztt.png');
	background-size:400% 400%;
	background-position:-1px -20px;
}
.status2{
	position:relative;
	display: inline-block;
	background:url('img/status/ztt.png');
	background-size:410% 410%;
	background-position:-21px -21px;
}

form.bind:

var obj = w('bmyj_show').getData();
var code = obj.code;
var height=w('tab0').style.height;

if(code == 'tab0'){
	w('frame0').height=height;
	w('frame0').showForm('bmyj_dy',null,null);
}else if(code == 'tab1'){
	w('frame1').height=height;
	w('frame1').showForm('bmyj_yy',null,null);
}
